<?phpnamespace App\Auth\Domain;use  App\Auth\Model\User as ModelUser;/** * 用户领域类 * * @author hms */class User{    private static $Model = null;    public function  __construct()    {        if (self::$Model == null) {            self::$Model = new ModelUser();        }    }        public function getUserInfo($uid) {        $r=self::$Model->getUserInfo($uid);        return $r;    } }